mapper.select:
select n, board_no,board_boardno,board_category,board_title,board_content,board_regdate,board_hit,board_pwd,board_fname,std_no from
		(select rownum n, board_no,board_boardno,board_category,board_title,
		board_content,board_regdate,board_hit,board_pwd,board_fname,std_no
		 from (select * from board order by board_no desc, board_regdate desc)
		where board_category like '%' ||#{board_category}|| '%' and board_boardno= #{board_boardno}

		 and '${keyword}' like '%' || '${search}' || '%'

		)where n >='${start}' and n <='${end}'

select count(*) from board where board_boardno=#{board_boardno} 
and board_category=#{board_category}

	and '${keyword}' like '%' || '${search}' || '%'

 order by board_no
